import React, { useContext, useRef, useState, useEffect } from 'react';
import './Navbar.css';
import { Link } from 'react-router-dom';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { ShopContext } from '../../Context/ShopContext';
import nav_dropdown from '../Assets/nav_dropdown.png';

const Navbar = () => {
  const { getTotalCartItems } = useContext(ShopContext);
  const [parentCategories, setParentCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [subCategories, setSubCategories] = useState([]);
  const [selectedSubCategory, setSelectedSubCategory] = useState(null);
  const [childCategories, setChildCategories] = useState([]);
  const menuRef = useRef();

  useEffect(() => {
    const fetchParentCategories = async () => {
      try {
        const response = await fetch('http://localhost:4000/Categories', {
          headers: {
            'Content-Type': 'application/json'
          }
        });
        if (response.ok) {
          const data = await response.json();
          setParentCategories(data);
        } else {
          throw new Error('Error al obtener las categorías principales');
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchParentCategories();
  }, []);

  const fetchCategories = async (categoryId, type) => {
    try {
      const response = await fetch(`http://localhost:4000/Categories/${categoryId}?type=${type}`, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      if (response.ok) {
        const data = await response.json();
        return data;
      } else {
        throw new Error('Error al obtener las categorías');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleCategoryClick = async (categoryId, event) => {
    event.preventDefault();
    if (selectedCategory === categoryId) {
      setSelectedCategory(null);
      setSubCategories([]);
    } else {
      setSelectedCategory(categoryId);
      const subCategoriesData = await fetchCategories(categoryId, 'subcategories');
      setSubCategories(subCategoriesData);
      setSelectedSubCategory(null);
      setChildCategories([]);
    }
  };

  const handleSubCategoryClick = async (subCategoryId, event) => {
    event.preventDefault();
    if (selectedSubCategory === subCategoryId) {
      setSelectedSubCategory(null);
      setChildCategories([]);
    } else {
      setSelectedSubCategory(subCategoryId);
      const childCategoriesData = await fetchCategories(subCategoryId, 'childCategories');
      setChildCategories(childCategoriesData);
    }
  };

  return (
    <div className='nav'>
      <Link to='/' onClick={() => setSelectedCategory(null)} style={{ textDecoration: 'none' }} className='nav-logo'>
        <img src={logo} alt='logo' />
        <p>SHOPPER</p>
      </Link>
      <img onClick={() => handleCategoryClick(null)} className='nav-dropdown' src={nav_dropdown} alt='' />
      <ul ref={menuRef} className='nav-menu'>
        {parentCategories.map((category) => (
          <li key={category.id}>
            <a href="/" onClick={(e) => handleCategoryClick(category.id, e)}>
              {category.nombre_categoria}
            </a>
            {selectedCategory === category.id && (
              <div className='submenu'>
                <ul>
                  {subCategories.map((subcategory) => (
                    <li key={subcategory.id}>
                      <a href="/" onClick={(e) => handleSubCategoryClick(subcategory.id, e)}>
                        {subcategory.nombre_categoria}
                      </a>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </li>
        ))}
      </ul>
      <div className='nav-login-cart'>
        {localStorage.getItem('auth-token') ? (
          <button onClick={() => {localStorage.removeItem('auth-token'); window.location.replace('/');}}>Logout</button>
        ) : (
          <Link to='/login' style={{ textDecoration: 'none' }}>
            <button>Login</button>
          </Link>
        )}
        <Link to='/cart'>
          <img src={cart_icon} alt='cart' />
        </Link>
        <div className='nav-cart-count'>{getTotalCartItems()}</div>
      </div>
      {/* Mostrar las subcategorías */}
      {childCategories.length > 0 && (
        <div className='submenu'>
          <ul>
            {childCategories.map((childCategory) => (
              <li key={childCategory.id}>
                <Link to={`/${childCategory.nombre_categoria}`} style={{ textDecoration: 'none' }}>
                  {childCategory.nombre_categoria}
                </Link>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Navbar;
