import React, { useEffect, useState } from "react";
import "./CSS/ShopCategory.css";
import dropdown_icon from '../Components/Assets/dropdown_icon.png'
import Item from "../Components/Item/Item";
import { Link } from "react-router-dom";

const ShopCategory = (props) => {
  const [filteredProducts, setFilteredProducts] = useState([]);

  const fetchInfo = () => { 
    fetch('http://localhost:4000/allproductsDisplay') 
      .then((res) => res.json()) 
      .then((data) => setFilteredProducts(data.filter(product => product.category === props.selectedCategory)))
  }

  useEffect(() => {
    fetchInfo();
  }, [props.selectedCategory]); // Fetch products when selected category changes

  return (
    <div className="shopcategory">
      <img src={props.banner} className="shopcategory-banner" alt="" />
      <div className="shopcategory-indexSort">
        <p><span>Showing 1 - {filteredProducts.length}</span> out of {filteredProducts.length} Products</p>
        <div className="shopcategory-sort">Sort by  <img src={dropdown_icon} alt="" /></div>
      </div>
      <div className="shopcategory-products">
        {filteredProducts.map((product) => (
          <Item
            key={product.id}
            id={product.id}
            name={product.nombre_producto}
            image={product.imagen_producto1}
            image2={product.imagen_producto2}
            image3={product.imagen_producto3}
            description={product.descripcion_producto}
          />
        ))}
      </div>
      <div className="shopcategory-loadmore">
        <Link to='/' style={{ textDecoration: 'none' }}>Explore More</Link>
      </div>
    </div>
  );
};

export default ShopCategory;
