import React, { useContext, useState, useEffect } from "react";
import "./ProductDisplay.css";
import star_icon from "../Assets/star_icon.png";
import star_dull_icon from "../Assets/star_dull_icon.png";
import { ShopContext } from "../../Context/ShopContext";

const ProductDisplay = (props) => {
  const { addToCart } = useContext(ShopContext);
  const [variationOptions1, setVariationOptions1] = useState([]);
  const [variationOptions2, setVariationOptions2] = useState([]);
  const [selectedOption1, setSelectedOption1] = useState(null);

  useEffect(() => {
    // Fetch para obtener las opciones del primer option list
    fetch(`http://localhost:4000/firstOptionList/${props.id}`)
      .then(response => response.json())
      .then(data => {
        setVariationOptions1(data.variationOptions1 || []);
        // Verificar si existe variationOptions2 en los datos recibidos
        setVariationOptions2(data.variationOptions2 || []);
      })
      .catch(error => console.error('Error fetching options:', error));
  }, [props.id]);

  useEffect(() => {
    // Si se selecciona una opciÃ³n en el primer option list, realizar el fetch para obtener las opciones del segundo option list
    if (selectedOption1) {
      fetch(`http://localhost:4000/secondOptionList/${selectedOption1}`)
        .then(response => response.json())
        .then(data => {
          // Verificar si data es un array
          if (Array.isArray(data)) {
            setVariationOptions2(data || []);
          } else {
            console.error('Invalid data format for variationOptions2');
          }
        })
        .catch(error => console.error('Error fetching second options:', error));
    }
  }, [selectedOption1]);

  return (
    <div className="productdisplay">
      <div className="productdisplay-left">
        <div className="productdisplay-img-list">
          <img src={props.image2} alt="img" />
          <img src={props.image3} alt="img" />
        </div>
        <div className="productdisplay-img">
          <img className="productdisplay-main-img" src={props.image1} alt="img" />
        </div>
      </div>
      <div className="productdisplay-right">
        <h1>{props.name}</h1>
        <div className="productdisplay-right-stars">
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_dull_icon} alt="" />
        </div>
        <div className="productdisplay-right-description">
          {props.description}
        </div>
        <div className="productdisplay-right-size">
          <h1>Select Variation Type 1</h1>
          <select onChange={(e) => setSelectedOption1(e.target.value)}>
            <option value="">Select...</option>
            {variationOptions1.map(option => (
              <option key={option.id} value={option.id}>{option.value}</option>
            ))}
          </select>
        </div>
        <div className="productdisplay-right-color">
          <h1>Select Variation Type 2</h1>
          <select>
            <option value="">Select...</option>
            {variationOptions2.map(option => (
              <option key={option.id} value={option.id}>{option.value}</option>
            ))}
          </select>
        </div>
        <button onClick={() => { addToCart(props.id) }}>ADD TO CART</button>
        <p className="productdisplay-right-category"><span>Category :</span> {props.category}</p>
        <p className="productdisplay-right-category"><span>Tags :</span> {props.tags}</p>
      </div>
    </div>
  );
};

export default ProductDisplay;
