import React, { useContext, useEffect, useRef, useState } from 'react';
import './Navbar.css';
import { Link } from 'react-router-dom';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { ShopContext } from '../../Context/ShopContext';
import nav_dropdown from '../Assets/nav_dropdown.png';

const Navbar = () => {
  let [menu, setMenu] = useState('shop');
  const { getTotalCartItems } = useContext(ShopContext);
  const [categorias, setCategorias] = useState([]);

  const menuRef = useRef();

  const dropdown_toggle = (e) => {
    menuRef.current.classList.toggle('nav-menu-visible');
    e.target.classList.toggle('open');
  };

  useEffect(() => {
    // Llamada al endpoint para obtener las categorías raíz
    fetch('http://localhost:4000/CategoriasRaiz')
      .then((response) => response.json())
      .then((data) => {
        setCategorias(data);
      })
      .catch((error) => console.error('Error al obtener las categorías:', error));
  }, []); // Se ejecuta solo una vez al cargar el componente

  return (
    <div className='nav'>
      <Link to='/' onClick={() => { setMenu('shop') }} style={{ textDecoration: 'none' }} className="nav-logo">
        <img src={logo} alt="logo" />
        <p>SHOPPER</p>
      </Link>
      <img onClick={dropdown_toggle} className='nav-dropdown' src={nav_dropdown} alt="" />
      <ul ref={menuRef} className="nav-menu">
        {categorias.map((categoria) => (
          <li key={categoria.id} onClick={() => { setMenu(categoria.nombre_categoria) }}>
            <Link to={`/${categoria.nombre_categoria.toLowerCase()}`} style={{ textDecoration: 'none' }}>
              {categoria.nombre_categoria}
            </Link>
            {menu === categoria.nombre_categoria ? <hr /> : <></>}
          </li>
        ))}
      </ul>
      <div className="nav-login-cart">
        {localStorage.getItem('auth-token')
          ? <button onClick={() => { localStorage.removeItem('auth-token'); window.location.replace("/"); }}>Logout</button>
          : <Link to='/login' style={{ textDecoration: 'none' }}><button>Login</button></Link>}
        <Link to="/cart"><img src={cart_icon} alt="cart" /></Link>
        <div className="nav-cart-count">{getTotalCartItems()}</div>
      </div>
    </div>
  );
};

export default Navbar;
