import React, { useContext, useEffect, useState } from "react";
import "./CartItems.css";
import cross_icon from "../Assets/cart_cross_icon.png";
import { ShopContext } from "../../Context/ShopContext";

const CartItems = () => {
  const { cartItems, removeFromCart } = useContext(ShopContext);
  const [displayedItems, setDisplayedItems] = useState([]);
  const [discounts, setDiscounts] = useState({});
  const [shippingMethod, setShippingMethod] = useState(1); // Método de envío predeterminado
  const [total, setTotal] = useState(3000); // Total de compra predeterminado
  const userId = localStorage.getItem("userId");
  const [addresses, setAddresses] = useState([]);
  const [shippingMethods, setShippingMethods] = useState([]);
  
  useEffect(() => {
    
    if (userId && userId !== "0") {
      fetch(`http://localhost:4000/cartItems/${userId}`)
        .then((res) => res.json())
        .then((data) => {
          setDisplayedItems(data);
        });
        fetch(`http://localhost:4000/userAddresses/${userId}`)
        .then((res) => res.json())
        .then((data) => {
          setAddresses(data);
        });
        fetch(`http://localhost:4000/shippingMethods`)
        .then((res) => res.json())
        .then((data) => {
          setShippingMethods(data);
        });
    }
  }, []);

    const handleAddNewAddress = () => {
    // Esta función podría implementarse para agregar una nueva dirección
    // de momento, no hará nada
  };

  const handleCheckout = () => {
    // Esta función podría implementarse para realizar el checkout
  };
  
  const handleDecrement = (itemId) => {
    // Update displayedItems locally
    setDisplayedItems((prevItems) =>
      prevItems.map((item) =>
        item.id === itemId && item.cantidad > 1
          ? { ...item, cantidad: item.cantidad - 1 }
          : item
      )
      
    );

    // Update quantity on the server
    fetch(`http://localhost:4000/cartItems/updateQuantity/${itemId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ operation: "decrement" }),
    })
      .catch((error) => console.error("Error:", error));
  };

  const handleIncrement = (itemId) => {
    // Update displayedItems locally
    setDisplayedItems((prevItems) =>
      prevItems.map((item) =>
        item.id === itemId ? { ...item, cantidad: item.cantidad + 1 } : item
      )
    );

    // Update quantity on the server
    fetch(`http://localhost:4000/cartItems/updateQuantity/${itemId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ operation: "increment" }),
    })
      .catch((error) => console.error("Error:", error));
  };


  return (
    <div className="cartitems">
      <div className="cartitems-format-main">
        <p>Imagen</p>
        <p>Producto</p>
        <p>Precio</p>
        <p>Cantidad</p>
        <p>Descuento</p>
        <p>Total</p>
        <p>Eliminar</p>
      </div>
      <hr />
      {displayedItems.map((item) => (
        <div key={item.id}>
          <div className="cartitems-format-main cartitems-format">
            <img
              className="cartitems-product-icon"
              src={item.imagen_producto1}
              alt=""
            />
            <p>{item.nombre_producto}</p>
            <p>Q.{(item.precio).toFixed(2)}</p>
            <div>
              <button
                onClick={() => handleDecrement(item.id)}
                disabled={item.cantidad <= 1}
              >
                -
              </button>
              <p>{item.cantidad}</p>
              <button onClick={() => handleIncrement(item.id)}>+</button>
            </div>
            <p>{item.descuento}%</p>
            <p>Q.{((item.precio - (item.precio * (item.descuento/100))) * item.cantidad).toFixed(2)}</p>
            <button
              onClick={() => {
                removeFromCart(item.id);
              }}
              className="cartitems-remove-icon"
            >
              <img src={cross_icon} alt="" />
            </button>
          </div>
          <hr />
        </div>
      ))}
      {/* Elementos adicionales para la dirección y checkout */}
      <div className="additional-elements">
        Dirreccion:   
        <select>
          {addresses.map((address, index) => (
            <option key={index} value={index}>{`${address.ciudad}, ${address.direccion}`}</option>
          ))}
        </select>
        <button onClick={handleAddNewAddress}>Agregar Nueva Dirección</button>
        <div>
          <label>
            Método de Envío:
            <select value={shippingMethod} onChange={(e) => setShippingMethod(e.target.value)}>
              {shippingMethods.map((method, index) => (
                <option key={index} value={method.nombre}>{`${method.nombre} - Q.${method.precio.toFixed(2)}`}</option>
              ))}
            </select>
          </label>
        </div>
        <div>
          <label>Total de la Compra:</label>
          <input type="text" value={`Q.${total.toFixed(2)}`} readOnly />
        </div>
        <button onClick={handleCheckout}>Checkout</button>
      </div>
    </div>
  );
};

export default CartItems;
