import React, { useEffect, useState } from "react";
import "./CSS/ShopCategory.css";
import dropdown_icon from '../Components/Assets/dropdown_icon.png'
import { useParams } from 'react-router-dom';

const ShopCategory = (props) => {
  const { categoryId } = useParams();
  const [categoryData, setCategoryData] = useState(null);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchCategoryData = async () => {
      try {
        const response = await fetch(`http://localhost:4000/Category/${categoryId}`);
        if (response.ok) {
          const data = await response.json();
          setCategoryData(data);
        } else {
          throw new Error('Error al obtener los datos de la categoría');
        }
      } catch (error) {
        console.error(error);
      }
    };

    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:4000/allproducts');
        if (response.ok) {
          const data = await response.json();
          setProducts(data.filter(product => product.nombre_categoria === categoryData?.nombre_categoria));
        } else {
          throw new Error('Error al obtener los productos');
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchCategoryData();
    fetchProducts();
  }, [categoryId, categoryData?.nombre_categoria]);

  return (
    <div>
      <h2>Categoría</h2>
      {categoryData ? (
        <div>
          <h3>{categoryData.nombre_categoria}</h3>
          <p>{categoryData.descripcion}</p>
          {/* Aquí puedes mostrar más detalles sobre la categoría si lo deseas */}
        </div>
      ) : (
        <p>Cargando...</p>
      )}

      <h2>Productos</h2>
      <div className="product-list">
        {products.map(product => (
          <div key={product.id} className="product-item">
            <img src={product.imagen_producto1} alt={product.nombre_producto} />
            <h3>{product.nombre_producto}</h3>
            <p>{product.descripcion_producto}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ShopCategory;
