import React, { useState, useEffect } from 'react';
import './Item.css';
import { Link } from 'react-router-dom';

const Item = (props) => {

  const [precio, setPrecio] = useState(null);

  useEffect(() => {
    fetch('/obtenerPrecio', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ productId: props.id })
    })
    .then(response => response.json())
    .then(data => {
      if (data.success) {
        setPrecio(data.precio);
      } else {
        console.error("Error al obtener el precio del producto:", data.errors);
      }
    })
    .catch(error => {
      console.error("Error de red al obtener el precio del producto:", error);
    });
  }, [props.id]);

    return (
    <div className='item'>
      <Link to={{ pathname: `/product/${props.id}`, state: { productId: props.id } }} style={{ textDecoration: 'none' }}>
        <img src={props.image} alt="products" />
      </Link>
      <p>{props.name}</p>
      {precio !== null && <p>Precio: {precio}</p>}
      <p>{props.image2}</p>
      <p>{props.image3}</p>
      <p>{props.idcat}</p>
    </div>
  );
};

export default Item;
