import React from "react";
import "./Hero.css";
import hero_image from "../Assets/hero_image.png";
import hand_icon from "../Assets/hand_icon.png";
import arrow_icon from "../Assets/arrow.png";

const Hero = () => {
  const [promoCategory, setPromoCategory] = useState("");

  useEffect(() => {
    fetch("/activePromotion")
      .then(response => response.json())
      .then(data => {
        if (data.categoryParentId) {
          fetch(`/category/${data.categoryParentId}`)
            .then(response => response.json())
            .then(categoryData => {
              setPromoCategory(categoryData.name);
            })
            .catch(error => console.error('Error fetching category:', error));
        }
      })
      .catch(error => console.error('Error fetching active promotion:', error));
  }, []);

  const handleClick = () => {
    fetch("/redirectPage/:categoryId/:subCategoryId")
      .then(response => {
        if (response.ok) {
          window.location.href = `/${promoCategory}/${subCategoryId}`;
        } else {
          console.error('Error redirecting to page:', response.status);
        }
      })
      .catch(error => console.error('Error redirecting to page:', error));
  };

  return (
    <div className="hero">
      <div className="hero-left">
        <h2>NUEVA PROMOCION</h2>
        <div>
          <div className="hero-hand-icon">
            <p>DESCUENTOS EN</p>
            <img src={hand_icon} alt="" />
          </div>
          <p>{promoCategory}</p>
        </div>
        <div className="hero-latest-btn" onClick={handleClick}>
          <div>VER</div>
          <img src={arrow_icon} alt="" />
        </div>
      </div>
      <div className="hero-right">
        <img src={hero_image} alt="hero" />
      </div>
    </div>
  );
};

export default Hero;
