import React, { useEffect, useState } from "react";
import "./CSS/ShopCategory.css";
import dropdown_icon from '../Components/Assets/dropdown_icon.png'
import Item from "../Components/Item/Item";
import { Link, useParams } from "react-router-dom"; // Agregar useParams

const ShopCategory = () => {
  const { category } = useParams(); // Obtener la categoría de los parámetros de la ruta

  const [allproducts, setAllProducts] = useState([]);

  const fetchInfo = () => { 
    fetch(`http://localhost:4000/allproductsDisplay/${category}`) // Usar la categoría de la ruta en la URL de la solicitud
            .then((res) => res.json()) 
            .then((data) => setAllProducts(data))
    }

    useEffect(() => {
      fetchInfo();
    }, [category]) // Agregar category como dependencia para que se vuelva a cargar cuando cambie

  return (
    <div className="shopcategory">
      <div className="shopcategory-indexSort">
        <p><span>Showing 1 - 12</span> out of 54 Products</p>
        <div className="shopcategory-sort">Sort by  <img src={dropdown_icon} alt="" /></div>
      </div>
      <div className="shopcategory-products">
        {allproducts.map((item,i) => (
          <Item id={item.id} key={i} name={item.name} image={item.image}  new_price={item.new_price} old_price={item.old_price}/>
        ))}
      </div>
      <div className="shopcategory-loadmore">
        <Link to='/' style={{ textDecoration: 'none' }}>Explore More</Link>
      </div>
    </div>
  );
};

export default ShopCategory;
