import React, { useContext, useRef, useState } from 'react';
import './Navbar.css';
import { Link } from 'react-router-dom';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { ShopContext } from '../../Context/ShopContext';
import nav_dropdown from '../Assets/nav_dropdown.png';

const Navbar = () => {
  const [menu, setMenu] = useState("shop");
  const { getTotalCartItems } = useContext(ShopContext);
  const menuRef = useRef();

  const [subMenuVisible, setSubMenuVisible] = useState({
    shop: false,
    mens: false,
    womens: false,
    kids: false,
  });

  const [subSubMenuVisible, setSubSubMenuVisible] = useState({
    mens: false,
    womens: false,
    kids: false,
  });

  const toggleSubMenu = (menuName) => {
    setSubMenuVisible((prevState) => ({
      ...prevState,
      [menuName]: !prevState[menuName],
    }));
    // Ocultar los submenús de otros elementos principales al mostrar uno nuevo
    for (const key in subMenuVisible) {
      if (key !== menuName) {
        setSubMenuVisible((prevState) => ({
          ...prevState,
          [key]: false,
        }));
      }
    }
  };

  const toggleSubSubMenu = (menuName) => {
    setSubSubMenuVisible((prevState) => ({
      ...prevState,
      [menuName]: !prevState[menuName],
    }));
    // Ocultar los submenús de otros elementos principales al mostrar uno nuevo
    for (const key in subSubMenuVisible) {
      if (key !== menuName) {
        setSubSubMenuVisible((prevState) => ({
          ...prevState,
          [key]: false,
        }));
      }
    }
  };

  const dropdownToggle = () => {
    menuRef.current.classList.toggle('nav-menu-visible');
  };

  return (
    <div className='nav'>
      <Link to='/' onClick={() => { setMenu("shop") }} style={{ textDecoration: 'none' }} className="nav-logo">
        <img src={logo} alt="logo" />
        <p>SHOPPER</p>
      </Link>
      <img onClick={dropdownToggle} className='nav-dropdown' src={nav_dropdown} alt="" />
      <ul ref={menuRef} className="nav-menu">
        <li
          onMouseEnter={() => toggleSubMenu("shop")}
          onMouseLeave={() => toggleSubMenu("shop")}
          className={subMenuVisible.shop ? 'show-sub-menu' : ''}
        >
          <Link to='/' style={{ textDecoration: 'none' }}>Shop</Link>
          <ul className="sub-menu">
            <li><Link to='/category1'>Category 1</Link></li>
            <li><Link to='/category2'>Category 2</Link></li>
          </ul>
        </li>
        <li
          onMouseEnter={() => toggleSubMenu("mens")}
          onMouseLeave={() => toggleSubMenu("mens")}
          className={subMenuVisible.mens ? 'show-sub-menu' : ''}
        >
          <Link to='/mens' style={{ textDecoration: 'none' }}>Men</Link>
          <ul className="sub-menu">
            <li
              onMouseEnter={() => toggleSubSubMenu("mens")}
              onMouseLeave={() => toggleSubSubMenu("mens")}
              className={subSubMenuVisible.mens ? 'show-sub-sub-menu' : ''}
            >
              <Link to='/mens/clothing'>Clothing</Link>
              <ul className="sub-sub-menu">
                <li><Link to='/mens/clothing/shirts'>Shirts</Link></li>
                <li><Link to='/mens/clothing/pants'>Pants</Link></li>
              </ul>
            </li>
          </ul>
        </li>
        {/* Repite el mismo patrón para las otras opciones principales */}
        {/* ... */}
      </ul>
      <div className="nav-login-cart">
        {localStorage.getItem('auth-token')
          ? <button onClick={() => { localStorage.removeItem('auth-token'); window.location.replace("/"); }}>Logout</button>
          : <Link to='/login' style={{ textDecoration: 'none' }}><button>Login</button></Link>}
        <Link to="/cart"><img src={cart_icon} alt="cart" /></Link>
        <div className="nav-cart-count">{getTotalCartItems()}</div>
      </div>
    </div>
  );
};

export default Navbar;
