import React, { useContext, useRef, useState, useEffect } from 'react';
import './Navbar.css';
import { Link } from 'react-router-dom';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { ShopContext } from '../../Context/ShopContext';
import nav_dropdown from '../Assets/nav_dropdown.png';

const Navbar = () => {
  const { getTotalCartItems } = useContext(ShopContext);
  const [parentCategories, setParentCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedSubCategory, setSelectedSubCategory] = useState(null);
  const [categoryHierarchy, setCategoryHierarchy] = useState([]);
  const menuRef = useRef();

  useEffect(() => {
    const fetchParentCategories = async () => {
      try {
        const response = await fetch('http://localhost:4000/ParentCategoriesNav');
        if (response.ok) {
          const data = await response.json();
          setParentCategories(data);
        } else {
          throw new Error('Error al obtener las categorías principales');
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchParentCategories();
  }, []);

  const fetchSubCategories = async (categoryId) => {
    try {
      const response = await fetch(`http://localhost:4000/SubCategories/${categoryId}`);
      if (response.ok) {
        const data = await response.json();
        return data;
      } else {
        throw new Error('Error al obtener las subcategorías');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleCategoryClick = async (categoryId, event) => {
    event.preventDefault();
    const hierarchy = await buildCategoryHierarchy(categoryId);
    setCategoryHierarchy(hierarchy);
    setSelectedSubCategory(null);
  };

  const buildCategoryHierarchy = async (categoryId) => {
    const subCategories = await fetchSubCategories(categoryId);
    if (subCategories && subCategories.length > 0) {
      return [{ id: categoryId, subCategories }];
    } else {
      return [{ id: categoryId, subCategories: null }];
    }
  };

  const handleSubCategoryClick = async (subCategoryId, event) => {
    event.preventDefault();
    const hierarchy = await buildCategoryHierarchy(subCategoryId);
    setCategoryHierarchy(hierarchy);
    setSelectedSubCategory(subCategoryId);
  };

  const handleBreadcrumbClick = async (index, event) => {
    event.preventDefault();
    const hierarchySlice = categoryHierarchy.slice(0, index + 1);
    setCategoryHierarchy(hierarchySlice);
    setSelectedSubCategory(null);
  };

  return (
    <div className='nav'>
      <Link to='/' onClick={() => setSelectedCategory(null)} style={{ textDecoration: 'none' }} className='nav-logo'>
        <img src={logo} alt='logo' />
        <p>SHOPPER</p>
      </Link>
      <img onClick={() => handleCategoryClick(null)} className='nav-dropdown' src={nav_dropdown} alt='' />
      <ul ref={menuRef} className='nav-menu'>
        {categoryHierarchy.map((level, index) => (
          <li key={level.id}>
            {index === categoryHierarchy.length - 1 ? (
              <span>{level.id}</span>
            ) : (
              <a href="/" onClick={(e) => handleBreadcrumbClick(index, e)}>
                {level.id}
              </a>
            )}
            {level.subCategories && (
              <div className='submenu'>
                <ul>
                  {level.subCategories.map((subcategory) => (
                    <li key={subcategory.id}>
                      <a href="/" onClick={(e) => handleSubCategoryClick(subcategory.id, e)}>
                        {subcategory.nombre_categoria}
                      </a>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </li>
        ))}
      </ul>
      <div className='nav-login-cart'>
        {localStorage.getItem('auth-token') ? (
          <button onClick={() => {localStorage.removeItem('auth-token'); window.location.replace('/');}}>Logout</button>
        ) : (
          <Link to='/login' style={{ textDecoration: 'none' }}>
            <button>Login</button>
          </Link>
        )}
        <Link to='/cart'>
          <img src={cart_icon} alt='cart' />
        </Link>
        <div className='nav-cart-count'>{getTotalCartItems()}</div>
      </div>
    </div>
  );
};

export default Navbar;
