import React, { useEffect, useState } from "react";
import "./CSS/ShopCategory.css";
import dropdown_icon from '../Components/Assets/dropdown_icon.png'
import Item from "../Components/Item/Item";
import { Link } from "react-router-dom";

const ShopCategory = ({ category, subCategory, banner }) => {

  const [products, setProducts] = useState([]);

  const fetchProductsBySubCategory = async (subCategoryId) => {
    try {
      const response = await fetch(`http://localhost:4000/productsBySubCategory/${subCategoryId}`);
      if (response.ok) {
        const data = await response.json();
        setProducts(data);
      } else {
        throw new Error('Error al obtener los productos por subcategoría');
      }
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    if (subCategory) {
      fetchProductsBySubCategory(subCategory);
    }
  }, [subCategory]);

  return (
    <div className="shop-category">
      <h2>{category}</h2>
      <img src={banner} alt="Banner" />
      <div className="products">
        {products.map((product) => (
          <Item
            key={product.id}
            id={product.id}
            name={product.nombre_producto}
            image={product.imagen_producto1}
            image2={product.imagen_producto2}
            image3={product.imagen_producto3}
            description={product.descripcion_producto}
          />
        ))}
      </div>
    </div>
  );
};

export default ShopCategory;
