import React, { useEffect, useState } from "react";
import "./CSS/ShopCategory.css";
import dropdown_icon from '../Components/Assets/dropdown_icon.png'
import Item from "../Components/Item/Item";
import { Link } from "react-router-dom";

const ShopCategory = ({ category, subCategory, banner }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(`http://localhost:4000/productsBySubCategory/${subCategory}`);
        if (response.ok) {
          const data = await response.json();
          setProducts(data);
        } else {
          throw new Error('Error al obtener los productos por subcategoría');
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchProducts();
  }, [subCategory]);

  return (
    <div className="shop-category">
      <h2>{category}</h2>
      {banner && <img src={banner} alt="banner" />}
      <div className="product-list">
        {products.map((item, index) => (
          <Item
            key={index}
            id={item.id}
            name={item.nombre_producto}
            image={item.imagen_producto1}
            image2={item.imagen_producto2}
            image3={item.imagen_producto3}
            description={item.descripcion_producto}
          />
        ))}
      </div>
    </div>
  );
};

export default ShopCategory;
