import React, { useContext, useEffect, useState } from "react";
import "./CartItems.css";
import cross_icon from "../Assets/cart_cross_icon.png";
import { ShopContext } from "../../Context/ShopContext";

const CartItems = () => {
  const { cartItems, removeFromCart } = useContext(ShopContext);
  const [displayedItems, setDisplayedItems] = useState([]);

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (userId && userId !== "0") {
      fetch(`http://localhost:4000/cartItems/${userId}`)
        .then((res) => res.json())
        .then((data) => {
          setDisplayedItems(data);
        });
    }
  }, []);

  // Función para agrupar los elementos del carrito por item
  const groupCartItems = (cartItems) => {
    const groupedItems = {};
    cartItems.forEach((item) => {
      const key = `${item.id_producto}_${item.nombre_producto}`;
      if (!groupedItems[key]) {
        groupedItems[key] = {
          ...item,
          cantidadTotal: item.cantidad,
        };
      } else {
        groupedItems[key].cantidadTotal += item.cantidad;
      }
    });
    return Object.values(groupedItems);
  };

  return (
    <div className="cartitems">
      <div className="cartitems-format-main">
        <p>Imagen</p>
        <p>Producto</p>
        <p>Variacion1</p>
        <p>Variacion2</p>
        <p>Precio</p>
        <p>Cantidad</p>
        <p>Descuento</p>
        <p>Eliminar</p>
      </div>
      <hr />
      {groupCartItems(displayedItems).map((item) => (
        <div key={item.id}>
          <div className="cartitems-format-main cartitems-format">
            <img
              className="cartitems-product-icon"
              src={item.imagen_producto1}
              alt=""
            />
            <p>{item.nombre_producto}</p>
            <p>{item.variacion1}</p>
            <p>{item.variacion2}</p>
            <p>${item.precio}</p>
            <p>{item.cantidadTotal}</p>
            <p>25%</p>
            <button
              onClick={() => {
                removeFromCart(item.id);
              }}
              className="cartitems-remove-icon"
            >
              <img src={cross_icon} alt="" />
            </button>
          </div>
          <hr />
        </div>
      ))}
    </div>
  );
};

export default CartItems;
