import React, { useEffect, useState } from 'react';
import "./CSS/ShopCategory.css";
import Item from '../Item/Item';

const ShopCategory = ({ idCategoria }) => {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    const fetchProductos = async () => {
      try {
        const response = await fetch(`http://localhost:4000/productos-por-categoria/${idCategoria}`);
        if (response.ok) {
          const data = await response.json();
          setProductos(data);
        } else {
          throw new Error('Error al obtener los productos de la categoría');
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchProductos();
  }, [idCategoria]);

  return (
    <div className='productos-categoria'>
      <h1>Productos de la Categoría</h1>
      <hr />
      <div className="productos">
        {productos.map((producto) => {
          return <Item
            key={producto.id}
            id={producto.id}
            name={producto.nombre_producto}
            image={producto.imagen_producto1}
            image2={producto.imagen_producto2}
            image3={producto.imagen_producto3}
            description={producto.descripcion_producto}
          />
        })}
      </div>
    </div>
  );
};

export default ShopCategory;
