import React from "react";
import "./Hero.css";
import hero_image from "../Assets/hero_image.png";
import hand_icon from "../Assets/hand_icon.png";
import arrow_icon from "../Assets/arrow.png";

const Hero = () => {

  const [promotion, setPromotion] = useState(null);
  const [category, setCategory] = useState(null);

  useEffect(() => {
    fetch("/currentPromotion")
      .then(response => response.json())
      .then(data => {
        setPromotion(data);
        fetch(`/category/${data.idCategoria}`)
          .then(response => response.json())
          .then(categoryData => setCategory(categoryData));
      })
      .catch(error => console.error('Error fetching promotion:', error));
  }, []);

  const handleLatestCollectionClick = () => {
    if (category) {
      const { idCategoriaPadre } = category;
      window.location.href = `/${idCategoriaPadre}/${promotion.id}`;
    }
  };

  return (
    <div className="hero">
      <div className="hero-left">
        <h2>NUEVA PROMOCION</h2>
        <div>
          <div className="hero-hand-icon">
            <p>DESCUENTOS EN</p>
            <img src={hand_icon} alt="" />
          </div>
          <p>{category ? category.nombreCategoria : "Loading..."}</p>
        </div>
        <div className="hero-latest-btn" onClick={handleLatestCollectionClick}>
          <div>VER</div>
          <img src={arrow_icon} alt="" />
        </div>
      </div>
      <div className="hero-right">
        <img src={hero_image} alt="hero" />
      </div>
    </div>
  );
};

export default Hero;
