import React, { useState, useEffect } from "react";
import "./CSS/LoginSignup.css";

const LoginSignup = () => {
  const [state, setState] = useState("Login");
  const [loginFormData, setLoginFormData] = useState({
    correo: "",
    contraseña: ""
  });
  const [signupFormData, setSignupFormData] = useState({
    nombre: "",
    apellido: "",
    correo: "",
    contraseña: "",
    telefono: "",
    direccion: "",
    estado: "",
    ciudad: "",
    codigo_postal: "",
    id_pais: ""
  });
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    fetch("http://localhost:4000/countries")
      .then((resp) => resp.json())
      .then((data) => {
        setCountries(data);
      })
      .catch((error) => {
        console.error("Error fetching countries:", error);
      });
  }, []);

  const changeHandler = (e, formType) => {
    const formData = formType === "login" ? loginFormData : signupFormData;
    const setter = formType === "login" ? setLoginFormData : setSignupFormData;
    setter({ ...formData, [e.target.name]: e.target.value });
  };

  const login = async () => {
    let dataObj;
    await fetch("http://localhost:4000/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        correo: loginFormData.correo,
        contraseña: loginFormData.contraseña
      }),
    })
      .then((resp) => resp.json())
      .then((data) => {
        dataObj = data;
      });

    if (dataObj.success) {
      localStorage.setItem("auth-token", dataObj.token);
      window.location.replace("/");
    } else {
      alert(dataObj.errors);
    }
  };

  const signup = async () => {
    let dataObj;
    const { direccion, estado, ciudad, codigo_postal, id_pais, ...userData } = signupFormData;
    await fetch("http://localhost:4000/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        ...userData,
        direccion_usuario: {
          direccion,
          estado,
          ciudad,
          codigo_postal,
          id_pais
        }
      }),
    })
      .then((resp) => resp.json())
      .then((data) => {
        dataObj = data;
      });

    if (dataObj.success) {
      localStorage.setItem("auth-token", dataObj.token);
      window.location.replace("/");
    } else {
      alert(dataObj.errors);
    }
  };

  return (
    <div className="loginsignup">
      <div className="loginsignup-container">
        <h1>{state}</h1>
        <div className="loginsignup-fields">
          {state === "Sign Up" && (
            <>
              <input
                type="text"
                placeholder="Nombre"
                name="nombre"
                value={signupFormData.nombre}
                onChange={(e) => changeHandler(e, "signup")}
              />
              <input
                type="text"
                placeholder="Apellido"
                name="apellido"
                value={signupFormData.apellido}
                onChange={(e) => changeHandler(e, "signup")}
              />
              <input
                type="text"
                placeholder="Telefono"
                name="telefono"
                value={signupFormData.telefono}
                onChange={(e) => changeHandler(e, "signup")}
              />
              <input
                type="text"
                placeholder="Dirección"
                name="direccion"
                value={formData.direccion}
                onChange={changeHandler}
              />
              <select name="id_pais" value={formData.id_pais} onChange={changeHandler}>
  <option value="">Seleccione un país</option>
  {countries.map((country) => (
    <option key={country.id} value={country.id}>
      {country.nombre_pais}
    </option>
  ))}
</select>
              
              <input
                type="text"
                placeholder="Ciudad"
                name="ciudad"
                value={formData.ciudad}
                onChange={changeHandler}
              />

              <input
                type="text"
                placeholder="Estado"
                name="estado"
                value={formData.estado}
                onChange={changeHandler}
              />
              <input
                type="text"
                placeholder="Código Postal"
                name="codigo_postal"
                value={formData.codigo_postal}
                onChange={changeHandler}
              />
            </>
          )}
          <input
            type="email"
            placeholder="Correo"
            name="correo"
            value={state === "Login" ? loginFormData.correo : signupFormData.correo}
            onChange={(e) => changeHandler(e, state === "Login" ? "login" : "signup")}
          />
          <input
            type="password"
            placeholder="Contraseña"
            name="contraseña"
            value={state === "Login" ? loginFormData.contraseña : signupFormData.contraseña}
            onChange={(e) => changeHandler(e, state === "Login" ? "login" : "signup")}
          />
          
        </div>

        <button onClick={() => (state === "Login" ? login() : signup())}>
          Continue
        </button>

        {state === "Login" ? (
          <p className="loginsignup-login">
            Create an account?{" "}
            <span onClick={() => setState("Sign Up")}>Click here</span>
          </p>
        ) : (
          <p className="loginsignup-login">
            Already have an account?{" "}
            <span onClick={() => setState("Login")}>Login here</span>
          </p>
        )}

        <div className="loginsignup-agree">
          <input type="checkbox" name="" id="" />
          <p>
            By continuing, I agree to the terms of use & privacy policy.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;
