import React, { useContext, useEffect, useState } from "react";
import "./CartItems.css";
import cross_icon from "../Assets/cart_cross_icon.png";
import { ShopContext } from "../../Context/ShopContext";

const CartItems = () => {
  const [displayedItems, setDisplayedItems] = useState([]);
  const [discounts, setDiscounts] = useState({});
  const [shippingMethod, setShippingMethod] = useState(1); // Método de envío predeterminado
  const [total, setTotal] = useState(3000); // Total de compra predeterminado
  const userId = localStorage.getItem("userId");
  const [addresses, setAddresses] = useState([]);
  const [shippingMethods, setShippingMethods] = useState([]);
  const [deliveryTime, setDeliveryTime] = useState("");
  const [showAddressForm, setShowAddressForm] = useState(false);
  const [newAddress, setNewAddress] = useState({
    direccion: "",
    estado: "",
    ciudad: "",
    codigo_postal: "",
    id_pais: ""
  });
  const [countries, setCountries] = useState([]);
  const [showPaymentForm, setShowPaymentForm] = useState(false); // Estado para controlar la visibilidad del formulario de método de pago
  const [newPaymentMethod, setNewPaymentMethod] = useState({
    numero_tarjeta: "",
    cvv: "",
    nombre_portador: "",
    fecha_expiracion: ""
  });
  const [validCardNumber, setValidCardNumber] = useState(false); // Estado para almacenar la validez del número de tarjeta
  const [validCVV, setValidCVV] = useState(false); // Estado para almacenar la validez del CVV
  const [validExpiryDate, setValidExpiryDate] = useState(false); // Estado para almacenar la validez de la fecha de expiración
  const [validForm, setValidForm] = useState(false); // Estado para controlar si el formulario puede ser enviado

  useEffect(() => {
    if (userId && userId !== "0") {
      fetch(`http://localhost:4000/cartItems/${userId}`)
        .then((res) => res.json())
        .then((data) => {
          setDisplayedItems(data);
        });
      fetch(`http://localhost:4000/userAddresses/${userId}`)
        .then((res) => res.json())
        .then((data) => {
          setAddresses(data);
        });
      fetch(`http://localhost:4000/shippingMethods`)
        .then((res) => res.json())
        .then((data) => {
          setShippingMethods(data);
        });
      fetch(`http://localhost:4000/countries`)
        .then((res) => res.json())
        .then((data) => {
          setCountries(data);
        });
    }
  }, []);

  const handleAddNewAddress = () => {
    setShowAddressForm(true);
  };

  const handleCancelAddress = () => {
    setShowAddressForm(false);
    setNewAddress({
      direccion: "",
      estado: "",
      ciudad: "",
      codigo_postal: "",
      id_pais: ""
    });
  };

  const handleSaveAddress = () => {
    // Guardar la nueva dirección en la base de datos
    fetch(`http://localhost:4000/addAddress`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ userId, ...newAddress }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          // Actualizar las direcciones mostradas después de agregar la nueva dirección
          setAddresses([...addresses, newAddress]);
          setShowAddressForm(false);
          setNewAddress({
            direccion: "",
            estado: "",
            ciudad: "",
            codigo_postal: "",
            id_pais: ""
          });
        } else {
          console.error('Error:', data.error);
        }
      })
      .catch((error) => console.error("Error:", error));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewAddress({ ...newAddress, [name]: value });
  };

  const handleCountryChange = (e) => {
    setNewAddress({ ...newAddress, id_pais: e.target.value });
  };

  const handleShippingMethodChange = (e) => {
    setShippingMethod(e.target.value);
    const selectedMethod = shippingMethods.find(method => method.nombre === e.target.value);
    if (selectedMethod) {
      switch (selectedMethod.id) {
        case 1:
          setDeliveryTime("De 4 a 5 días de entrega");
          break;
        case 2:
          setDeliveryTime("De 1 a 2 días de entrega");
          break;
        case 3:
          setDeliveryTime("De 1 a 3 días de entrega");
          break;
        default:
          setDeliveryTime("");
          break;
      }
    }
  };

  const handleRemoveItem = async (userId, itemId) => {
    try {
      const response = await fetch('http://localhost:4000/removeCartItem', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ userId, itemId })
      });
  
      const data = await response.json();
  
      if (data.success) {
        // Actualizar los ítems mostrados después de la eliminación
        const updatedItems = displayedItems.filter(item => item.id !== itemId);
        setDisplayedItems(updatedItems);
      } else {
        console.error('Error:', data.error);
      }
    } catch (error) {
      console.error('Error removing item:', error);
    }
  };

  const handleCardNumberChange = (e) => {
    const cardNumber = e.target.value;
    // Validar que el número de tarjeta contenga solo números y tenga una longitud máxima de 16 dígitos
    const isValid = /^\d{0,16}$/.test(cardNumber);
    setValidCardNumber(isValid);
    setNewPaymentMethod({ ...newPaymentMethod, numero_tarjeta: cardNumber });
  };

  const handleCVVChange = (e) => {
    const cvv = e.target.value;
    // Validar que el CVV contenga solo números y tenga una longitud máxima de 3 dígitos
    const isValid = /^\d{0,3}$/.test(cvv);
    setValidCVV(isValid);
    setNewPaymentMethod({ ...newPaymentMethod, cvv });
  };

  const handleNameChange = (e) => {
    const name = e.target.value;
    setNewPaymentMethod({ ...newPaymentMethod, nombre_portador: name });
  };

  const handleExpiryDateChange = (e) => {
    let expiryDate = e.target.value;
    // Validar que la fecha de expiración tenga el formato MM/AA y solo contenga números
    expiryDate = expiryDate.replace(/\D/g, '');
    // Insertar "/" después de ingresar los primeros 2 dígitos del mes
    if (expiryDate.length >= 2) {
      expiryDate = expiryDate.slice(0, 2) + '/' + expiryDate.slice(2);
    }
    setNewPaymentMethod({ ...newPaymentMethod, fecha_expiracion: expiryDate });

    // Validar la fecha de expiración
    if (expiryDate.length === 5) {
      const [month, year] = expiryDate.split('/');
      const currentYear = new Date().getFullYear().toString().slice(-2);
      const currentMonth = new Date().getMonth() + 1;

      // Verificar si el mes ingresado es válido (01-12)
      const validMonth = parseInt(month) >= 1 && parseInt(month) <= 12;
      // Verificar si el año ingresado es válido (igual o posterior al año actual)
      const validYear = parseInt(year) >= parseInt(currentYear);
      // Verificar si la fecha es válida
      const isValidDate = validMonth && validYear && (parseInt(year) !== parseInt(currentYear) || parseInt(month) > currentMonth);

      setValidExpiryDate(isValidDate);
    } else {
      setValidExpiryDate(false);
    }
  };

  const handlePaymentFormSubmit = () => {
    // Aquí puedes enviar los datos del método de pago al servidor y realizar las operaciones necesarias
    // Por ejemplo:
    // fetch('URL_DEL_ENDPOINT', {
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify(newPaymentMethod),
    // })
    // .then(response => response.json())
    // .then(data => {
    //   // Manejar la respuesta del servidor
    // })
    // .catch(error => {
    //   console.error('Error al enviar el método de pago:', error);
    // });

    // Una vez que se envíen los datos, puedes reiniciar el formulario y ocultar el formulario de método de pago
    setNewPaymentMethod({
      numero_tarjeta: "",
      cvv: "",
      nombre_portador: "",
      fecha_expiracion: ""
    });
    setShowPaymentForm(false);
  };

  const handleCancelPayment = () => {
    // Reiniciar el formulario y ocultar el formulario de método de pago
    setNewPaymentMethod({
      numero_tarjeta: "",
      cvv: "",
      nombre_portador: "",
      fecha_expiracion: ""
    });
    setShowPaymentForm(false);
  };

  const calculateTotal = () => {
    // Sumar los totales de todos los items del carrito
    const itemsTotal = displayedItems.reduce((total, item) => {
      return total + ((item.precio - (item.precio * (item.descuento/100))) * item.cantidad);
    }, 0);
  
    // Obtener el precio del método de envío escogido
    const selectedMethod = shippingMethods.find(method => method.nombre === shippingMethod);
    const shippingPrice = selectedMethod ? selectedMethod.precio : 0;
  
    // Sumar el precio del método de envío al total de la compra
    const total = itemsTotal + shippingPrice;
  
    return total.toFixed(2);
  };

  return (
    <div className="cartitems">
      <div className="cartitems-format-main">
        <p>Imagen</p>
        <p>Producto</p>
        <p>Precio</p>
        <p>Cantidad</p>
        <p>Descuento</p>
        <p>Total</p>
        <p>Eliminar</p>
      </div>
      <hr />
      {displayedItems.map((item) => (
        <div key={item.id}>
          <div className="cartitems-format-main cartitems-format">
            <img
              className="cartitems-product-icon"
              src={item.imagen_producto1}
              alt=""
            />
            <p>{item.nombre_producto}</p>
            <p>Q.{(item.precio).toFixed(2)}</p>
            <div>
              <button
                onClick={() => handleDecrement(item.id)}
                disabled={item.cantidad <= 1}
              >
                -
              </button>
              <p>{item.cantidad}</p>
              <button onClick={() => handleIncrement(item.id)}>+</button>
            </div>
            <p>{item.descuento}%</p>
            <p>Q.{((item.precio - (item.precio * (item.descuento/100))) * item.cantidad).toFixed(2)}</p>
            <button
              onClick={() => {
                handleRemoveItem(userId, item.id);
              }}
              className="cartitems-remove-icon"
            >
              <img src={cross_icon} alt="" />
            </button>
          </div>
          <hr />
        </div>
      ))}
      {showAddressForm && (
        <div>
          <h3>Nueva Dirección</h3>
          <div>
            <label>Dirección:</label>
            <input
              type="text"
              name="direccion"
              value={newAddress.direccion}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Estado:</label>
            <input
              type="text"
              name="estado"
              value={newAddress.estado}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Ciudad:</label>
            <input
              type="text"
              name="ciudad"
              value={newAddress.ciudad}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Código Postal:</label>
            <input
              type="text"
              name="codigo_postal"
              value={newAddress.codigo_postal}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>País:</label>
            <select value={newAddress.id_pais} onChange={handleCountryChange}>
              <option value="">Seleccionar País</option>
              {countries.map((country) => (
                <option key={country.id} value={country.id}>
                  {country.nombre_pais}
                </option>
              ))}
            </select>
          </div>
          <div>
            <button onClick={handleSaveAddress}>Guardar</button>
            <button onClick={handleCancelAddress}>Cancelar</button>
          </div>
        </div>
      )}
      {/* Elementos adicionales para la dirección y checkout */}
      <div className="additional-elements">
        Dirección:   
        <select>
          {addresses.map((address, index) => (
            <option key={index} value={index}>{`${address.ciudad}, ${address.direccion}`}</option>
          ))}
        </select>
        <button onClick={handleAddNewAddress}>Agregar Nueva Dirección</button>
        <div>
          <label>
            Método de Envío:
            <select value={shippingMethod} onChange={handleShippingMethodChange}>
              {shippingMethods.map((method, index) => (
                <option key={index} value={method.nombre}>{`${method.nombre} - Q.${method.precio}`}</option>
              ))}
            </select>
            {deliveryTime}
          </label>
        </div>
        <div>
          <label>Total de la Compra:</label>
          <input type="text" value={`Q.${calculateTotal()}`} readOnly />
        </div>
        <button onClick={() => setShowPaymentForm(true)}>Agregar Método de Pago</button>
        {showPaymentForm && (
          <div>
            <h3>Nuevo Método de Pago</h3>
            <div>
              <label>Número de Tarjeta:</label>
              <input
                type="text"
                value={newPaymentMethod.numero_tarjeta}
                onChange={handleCardNumberChange}
                maxLength="16"
              />
              {!validCardNumber && <p style={{ color: 'red' }}>Número de tarjeta inválido</p>}
            </div>
            <div>
              <label>CVV:</label>
              <input
                type="text"
                value={newPaymentMethod.cvv}
                onChange={handleCVVChange}
                maxLength="3"
              />
              {!validCVV && <p style={{ color: 'red' }}>CVV inválido</p>}
            </div>
            <div>
              <label>Nombre del Portador:</label>
              <input
                type="text"
                value={newPaymentMethod.nombre_portador}
                onChange={handleNameChange}
              />
            </div>
            <div>
              <label>Fecha de Expiración (MM/AA):</label>
              <input
                type="text"
                value={newPaymentMethod.fecha_expiracion}
                onChange={handleExpiryDateChange}
                maxLength="5"
              />
              {!validExpiryDate && <p style={{ color: 'red' }}>Fecha de expiración inválida</p>}
            </div>
            <button onClick={handlePaymentFormSubmit} disabled={!validCardNumber || !validCVV || !validExpiryDate}>Guardar</button>
            <button onClick={handleCancelPayment}>Cancelar</button>
          </div>
        )}
      </div>
    </div>
  );

};

export default CartItems;
