import React, { useContext, useRef, useState, useEffect } from 'react';
import './Navbar.css';
import { Link } from 'react-router-dom';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { ShopContext } from '../../Context/ShopContext';
import nav_dropdown from '../Assets/nav_dropdown.png';

const Navbar = () => {
  const [menu, setMenu] = useState('shop');
  const { getTotalCartItems } = useContext(ShopContext);
  const [parentCategories, setParentCategories] = useState([]);
  const [subCategories, setSubCategories] = useState([]); // Nuevo estado para almacenar las subcategorías
  const menuRef = useRef();

  useEffect(() => {
    const fetchParentCategories = async () => {
      try {
        const response = await fetch('http://localhost:4000/ParentCategoriesNav');
        if (response.ok) {
          const data = await response.json();
          setParentCategories(data);
        } else {
          throw new Error('Error al obtener las categorías principales');
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchParentCategories();
  }, []);

  // Función para obtener las subcategorías cuando se hace clic en una categoría principal
  const fetchSubCategories = async (parentId) => {
    try {
      const response = await fetch(`http://localhost:4000/SubCategories/${parentId}`);
      if (response.ok) {
        const data = await response.json();
        setSubCategories(data);
      } else {
        throw new Error('Error al obtener las subcategorías');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const dropdown_toggle = (e, parentId) => {
    fetchSubCategories(parentId); // Obtener las subcategorías al hacer clic
    menuRef.current.classList.toggle('nav-menu-visible');
    e.target.classList.toggle('open');
  };

  return (
    <div className='nav'>
      <Link to='/' onClick={() => setMenu('shop')} style={{ textDecoration: 'none' }} className='nav-logo'>
        <img src={logo} alt='logo' />
        <p>SHOPPER</p>
      </Link>
      <img onClick={(e) => dropdown_toggle(e, 'main')} className='nav-dropdown' src={nav_dropdown} alt='' />
      <ul ref={menuRef} className='nav-menu'>
        {parentCategories.map((category) => (
          <li key={category.id} onClick={(e) => dropdown_toggle(e, category.id)}>
            <Link to={`/${category.nombre_categoria}`} style={{ textDecoration: 'none' }}>
              {category.nombre_categoria}
            </Link>
            {menu === category.nombre_categoria ? <hr /> : <></>}
          </li>
        ))}
      </ul>
      <div className='nav-login-cart'>
        {localStorage.getItem('auth-token') ? (
          <button onClick={() => {localStorage.removeItem('auth-token'); window.location.replace('/');}}>Logout</button>
        ) : (
          <Link to='/login' style={{ textDecoration: 'none' }}>
            <button>Login</button>
          </Link>
        )}
        <Link to='/cart'>
          <img src={cart_icon} alt='cart' />
        </Link>
        <div className='nav-cart-count'>{getTotalCartItems()}</div>
      </div>
      {/* Mostrar las subcategorías en un menú desplegable */}
      <ul className='sub-menu'>
        {subCategories.map((subcategory) => (
          <li key={subcategory.id}>
            <Link to={`/${subcategory.nombre_categoria}`} style={{ textDecoration: 'none' }}>
              {subcategory.nombre_categoria}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Navbar;
