import React, { useEffect, useState } from "react";
import "./CSS/ShopCategory.css";
import dropdown_icon from '../Components/Assets/dropdown_icon.png'
import Item from "../Components/Item/Item";
import { Link } from "react-router-dom";

const ShopCategory = ({ category, subCategory, banner }) => {

  const [allproducts, setAllProducts] = useState([]);

  const fetchInfo = () => { 
    fetch('http://localhost:4000/allproductsDisplay') 
            .then((res) => res.json()) 
            .then((data) => setAllProducts(data))
    }

  useEffect(() => {
    fetchInfo();
  }, []);

  return (
    <div className="shop-category">
      {/* Agrega el banner si lo deseas */}
      {banner && (
        <div className="banner">
          <img src={banner} alt="Banner" />
        </div>
      )}
      
      {/* Mostrar productos */}
      <div className="product-list">
        {allproducts.map((item, index) => (
          <Item
            key={index}
            id={item.id}
            name={item.nombre_producto}
            image={item.imagen_producto1}
            image2={item.imagen_producto2}
            image3={item.imagen_producto3}
            description={item.descripcion_producto}
          />
        ))}
      </div>
    </div>
  );
};

export default ShopCategory;
