import React, { useState, useEffect } from 'react';
import './Navbar.css';
import { Link } from 'react-router-dom';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { ShopContext } from '../../Context/ShopContext';
import nav_dropdown from '../Assets/nav_dropdown.png';

const Navbar = () => {
  const [menu, setMenu] = useState('shop');
  const [categories, setCategories] = useState([]);
  const { getTotalCartItems } = useContext(ShopContext);

  useEffect(() => {
    // Hacer la solicitud para obtener las categorías sin padre definido
    fetch('/categoriasSinPadre')
      .then((response) => response.json())
      .then((data) => {
        setCategories(data);
      })
      .catch((error) => {
        console.error('Error al obtener las categorías:', error);
      });
  }, []); // Este efecto se ejecutará solo una vez al montar el componente

  const dropdown_toggle = (e) => {
    // Implementa tu lógica para mostrar/ocultar el menú desplegable aquí
  };

  return (
    <div className="nav">
      <Link to="/" onClick={() => { setMenu('shop'); }} style={{ textDecoration: 'none' }} className="nav-logo">
        <img src={logo} alt="logo" />
        <p>SHOPPER</p>
      </Link>
      <img onClick={dropdown_toggle} className="nav-dropdown" src={nav_dropdown} alt="" />
      <ul className="nav-menu">
        {categories.map((category) => (
          <li key={category.id} onClick={() => { setMenu(category.nombre_categoria.toLowerCase()); }}>
            <Link to={`/${category.nombre_categoria.toLowerCase()}`} style={{ textDecoration: 'none' }}>
              {category.nombre_categoria}
            </Link>
            {menu === category.nombre_categoria.toLowerCase() ? <hr /> : <></>}
          </li>
        ))}
      </ul>
      <div className="nav-login-cart">
        {localStorage.getItem('auth-token') ? (
          <button onClick={() => { localStorage.removeItem('auth-token'); window.location.replace('/'); }}>Logout</button>
        ) : (
          <Link to="/login" style={{ textDecoration: 'none' }}>
            <button>Login</button>
          </Link>
        )}
        <Link to="/cart"><img src={cart_icon} alt="cart" /></Link>
        <div className="nav-cart-count">{getTotalCartItems()}</div>
      </div>
    </div>
  );
};

export default Navbar;
