import React, { useContext, useRef, useState, useEffect } from 'react';
import './Navbar.css';
import { Link } from 'react-router-dom';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { ShopContext } from '../../Context/ShopContext';
import nav_dropdown from '../Assets/nav_dropdown.png';

const Navbar = () => {
  const { getTotalCartItems } = useContext(ShopContext);
  const [parentCategories, setParentCategories] = useState([]);
  const menuRef = useRef();

  useEffect(() => {
    const fetchParentCategories = async () => {
      try {
        const response = await fetch('http://localhost:4000/ParentCategoriesNav');
        if (response.ok) {
          const data = await response.json();
          setParentCategories(data);
        } else {
          throw new Error('Error al obtener las categorías principales');
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchParentCategories();
  }, []); // Se ejecuta solo una vez al montar el componente

  const dropdown_toggle = (e) => {
    menuRef.current.classList.toggle('nav-menu-visible');
    e.target.classList.toggle('open');
  };

  return (
    <div className='nav'>
      <Link to='/' style={{ textDecoration: 'none' }} className='nav-logo'>
        <img src={logo} alt='logo' />
        <p>SHOPPER</p>
      </Link>
      <img onClick={dropdown_toggle} className='nav-dropdown' src={nav_dropdown} alt='' />
      <ul ref={menuRef} className='nav-menu'>
        {parentCategories.map((category) => (
          <li key={category.id} onClick={() => console.log(category.nombre_categoria)}>
            <Link to={`/${category.nombre_categoria}`} style={{ textDecoration: 'none' }}>
              {category.nombre_categoria}
            </Link>
          </li>
        ))}
      </ul>
      <div className='nav-login-cart'>
        {localStorage.getItem('auth-token') ? (
          <button onClick={() => {localStorage.removeItem('auth-token'); window.location.replace('/');}}>Logout</button>
        ) : (
          <Link to='/login' style={{ textDecoration: 'none' }}>
            <button>Login</button>
          </Link>
        )}
        <Link to='/cart'>
          <img src={cart_icon} alt='cart' />
        </Link>
        <div className='nav-cart-count'>{getTotalCartItems()}</div>
      </div>
    </div>
  );
};

export default Navbar;
