import React, { useContext, useEffect, useState } from "react";
import "./CartItems.css";
import cross_icon from "../Assets/cart_cross_icon.png";
import { ShopContext } from "../../Context/ShopContext";

const CartItems = () => {
  const [displayedItems, setDisplayedItems] = useState([]);
  const [discounts, setDiscounts] = useState({});
  const [shippingMethod, setShippingMethod] = useState(1); // Método de envío predeterminado
  const [total, setTotal] = useState(3000); // Total de compra predeterminado
  const userId = localStorage.getItem("userId");
  const [addresses, setAddresses] = useState([]);
  const [shippingMethods, setShippingMethods] = useState([]);
  const [deliveryTime, setDeliveryTime] = useState("");
  const [showAddressForm, setShowAddressForm] = useState(false);
  const [showPaymentForm, setShowPaymentForm] = useState(false);
  const [newAddress, setNewAddress] = useState({
    direccion: "",
    estado: "",
    ciudad: "",
    codigo_postal: "",
    id_pais: ""
  });
  const [newPaymentMethod, setNewPaymentMethod] = useState({ // Estado para los datos del nuevo método de pago
    numero_tarjeta: "",
    cvv: "",
    nombre_portador: "",
    fecha_expiracion: ""
  });
  const [countries, setCountries] = useState([]);

 const [paymentFormValid, setPaymentFormValid] = useState(false); // Estado para rastrear la validez del formulario de método de pago
  const [expirationDateError, setExpirationDateError] = useState("");

  const validateCardNumber = (value) => {
    return /^\d{0,16}$/.test(value); // Acepta solo números y máximo 16 caracteres
  };

  // Función para validar el CVV
  const validateCVV = (value) => {
    return /^\d{0,3}$/.test(value); // Acepta solo números y máximo 3 caracteres
  };

  // Función para validar la fecha de expiración
  const validateExpirationDate = (value) => {
    const [month, year] = value.split("/");
    const currentYear = new Date().getFullYear().toString().slice(-2); // Obtener los últimos 2 dígitos del año actual

    if (month.length === 2 && year.length === 2) {
      // Verificar si los primeros 2 dígitos representan un mes válido (01-12)
      if (parseInt(month) < 1 || parseInt(month) > 12) {
        setExpirationDateError("El mes debe estar entre 01 y 12");
        return false;
      }

      // Verificar si los últimos 2 dígitos son mayores o iguales al año actual
      if (parseInt(year) < parseInt(currentYear)) {
        setExpirationDateError("El año de expiración no puede ser anterior al año actual");
        return false;
      }

      // Si la fecha es válida, eliminar el mensaje de error
      setExpirationDateError("");
      return true;
    }

    return false;
  };

  

  useEffect(() => {
    
    if (userId && userId !== "0") {
      fetch(`http://localhost:4000/cartItems/${userId}`)
        .then((res) => res.json())
        .then((data) => {
          setDisplayedItems(data);
        });
        fetch(`http://localhost:4000/userAddresses/${userId}`)
        .then((res) => res.json())
        .then((data) => {
          setAddresses(data);
        });
        fetch(`http://localhost:4000/shippingMethods`)
        .then((res) => res.json())
        .then((data) => {
          setShippingMethods(data);
        });
        fetch(`http://localhost:4000/countries`)
        .then((res) => res.json())
        .then((data) => {
          setCountries(data);
        });
    }
  }, []);

  const handleAddNewAddress = () => {
    setShowAddressForm(true);
  };

  const handleCancelAddress = () => {
    setShowAddressForm(false);
    setNewAddress({
      direccion: "",
      estado: "",
      ciudad: "",
      codigo_postal: "",
      id_pais: ""
    });
  };

  const handleSaveAddress = () => {
    // Guardar la nueva dirección en la base de datos
    fetch(`http://localhost:4000/addAddress`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ userId, ...newAddress }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          // Actualizar las direcciones mostradas después de agregar la nueva dirección
          setAddresses([...addresses, newAddress]);
          setShowAddressForm(false);
          setNewAddress({
            direccion: "",
            estado: "",
            ciudad: "",
            codigo_postal: "",
            id_pais: ""
          });
        } else {
          console.error('Error:', data.error);
        }
      })
      .catch((error) => console.error("Error:", error));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewAddress({ ...newAddress, [name]: value });
  };

  const handleCountryChange = (e) => {
    setNewAddress({ ...newAddress, id_pais: e.target.value });
  };

  const handleCheckout = async () => {
    try {
      const response = await fetch('http://localhost:4000/checkout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          userId,
          shippingMethod,
          cartItems: displayedItems.map(item => ({ id_item_producto: item.id, cantidad: item.cantidad }))
        })
      });

      const data = await response.json();

      if (data.error) {
        alert(data.error); // Mostrar mensaje de error en caso de existencias insuficientes
      } else {
        // Si todas las condiciones son exitosas, mostrar el formulario de método de pago
        setShowPaymentForm(true);
      }
    } catch (error) {
      console.error('Error en el proceso de checkout:', error);
      alert("Ocurrió un error al procesar el checkout.");
    }
  };

  const handlePaymentInputChange = (e) => {
    const { name, value } = e.target;
    setNewPaymentMethod({ ...newPaymentMethod, [name]: value });

    // Validar el número de tarjeta
    if (name === "numero_tarjeta") {
      setPaymentFormValid(validateCardNumber(value));
    }

    // Validar el CVV
    if (name === "cvv") {
      setPaymentFormValid(validateCVV(value));
    }

    // Validar la fecha de expiración
    if (name === "fecha_expiracion") {
      setPaymentFormValid(validateExpirationDate(value));
    }
  };



  const handlePaymentFormSubmit = async () => {
    try {
      const response = await fetch('http://localhost:4000/addPaymentMethod', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          userId,
          ...newPaymentMethod
        })
      });

      const data = await response.json();

      if (data.success) {
        // Aquí podrías redirigir al usuario a una página de confirmación de compra o mostrar un mensaje de éxito
        alert("Método de pago registrado exitosamente.");
      } else {
        console.error('Error:', data.error);
      }
    } catch (error) {
      console.error('Error en el proceso de registro del método de pago:', error);
      alert("Ocurrió un error al registrar el método de pago.");
    }
  };
  
  const handleDecrement = (itemId) => {
    // Update displayedItems locally
    setDisplayedItems((prevItems) =>
      prevItems.map((item) =>
        item.id === itemId && item.cantidad > 1
          ? { ...item, cantidad: item.cantidad - 1 }
          : item
      )
      
    );

    // Update quantity on the server
    fetch(`http://localhost:4000/cartItems/updateQuantity/${itemId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ operation: "decrement" }),
    })
      .catch((error) => console.error("Error:", error));
  };

  const handleIncrement = (itemId) => {
    // Update displayedItems locally
    setDisplayedItems((prevItems) =>
      prevItems.map((item) =>
        item.id === itemId ? { ...item, cantidad: item.cantidad + 1 } : item
      )
    );

    // Update quantity on the server
    fetch(`http://localhost:4000/cartItems/updateQuantity/${itemId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ operation: "increment" }),
    })
      .catch((error) => console.error("Error:", error));

      
  };

  const handleShippingMethodChange = (e) => {
    setShippingMethod(e.target.value);
    const selectedMethod = shippingMethods.find(method => method.nombre === e.target.value);
    if (selectedMethod) {
      switch (selectedMethod.id) {
        case 1:
          setDeliveryTime("De 4 a 5 días de entrega");
          break;
        case 2:
          setDeliveryTime("De 1 a 2 días de entrega");
          break;
        case 3:
          setDeliveryTime("De 1 a 3 días de entrega");
          break;
        default:
          setDeliveryTime("");
          break;
      }
    }
  };

  const handleRemoveItem = async (userId, itemId) => {
    try {
      const response = await fetch('http://localhost:4000/removeCartItem', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ userId, itemId })
      });
  
      const data = await response.json();
  
      if (data.success) {
        // Actualizar los ítems mostrados después de la eliminación
        const updatedItems = displayedItems.filter(item => item.id !== itemId);
        setDisplayedItems(updatedItems);
      } else {
        console.error('Error:', data.error);
      }
    } catch (error) {
      console.error('Error removing item:', error);
    }
  };

  const calculateTotal = () => {
    // Sumar los totales de todos los items del carrito
    const itemsTotal = displayedItems.reduce((total, item) => {
      return total + ((item.precio - (item.precio * (item.descuento/100))) * item.cantidad);
    }, 0);
  
    // Obtener el precio del método de envío escogido
    const selectedMethod = shippingMethods.find(method => method.nombre === shippingMethod);
    const shippingPrice = selectedMethod ? selectedMethod.precio : 0;
  
    // Sumar el precio del método de envío al total de la compra
    const total = itemsTotal + shippingPrice;
  
    return total.toFixed(2);
  };

   // Función para manejar la cancelación del formulario de método de pago
   const handleCancelPayment = () => {
    setShowPaymentForm(false);
    // Restablecer los campos del formulario de método de pago
    setNewPaymentMethod({
      numero_tarjeta: "",
      cvv: "",
      nombre_portador: "",
      fecha_expiracion: ""
    });
    // Restablecer la validez del formulario
    setPaymentFormValid(false);
    // Restablecer el mensaje de error de fecha de expiración
    setExpirationDateError("");
  };

  return (
    <div className="cartitems">
      <div className="cartitems-format-main">
        <p>Imagen</p>
        <p>Producto</p>
        <p>Precio</p>
        <p>Cantidad</p>
        <p>Descuento</p>
        <p>Total</p>
        <p>Eliminar</p>
      </div>
      <hr />
      {displayedItems.map((item) => (
        <div key={item.id}>
          <div className="cartitems-format-main cartitems-format">
            <img
              className="cartitems-product-icon"
              src={item.imagen_producto1}
              alt=""
            />
            <p>{item.nombre_producto}</p>
            <p>Q.{(item.precio).toFixed(2)}</p>
            <div>
              <button
                onClick={() => handleDecrement(item.id)}
                disabled={item.cantidad <= 1}
              >
                -
              </button>
              <p>{item.cantidad}</p>
              <button onClick={() => handleIncrement(item.id)}>+</button>
            </div>
            <p>{item.descuento}%</p>
            <p>Q.{((item.precio - (item.precio * (item.descuento/100))) * item.cantidad).toFixed(2)}</p>
            <button
              onClick={() => {
                handleRemoveItem(userId, item.id);
              }}
              className="cartitems-remove-icon"
            >
              <img src={cross_icon} alt="" />
            </button>
          </div>
          <hr />
        </div>
      ))}
      {showAddressForm && (
        <div>
          <h3>Nueva Dirección</h3>
          <div>
            <label>Dirección:</label>
            <input
              type="text"
              name="direccion"
              value={newAddress.direccion}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Estado:</label>
            <input
              type="text"
              name="estado"
              value={newAddress.estado}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Ciudad:</label>
            <input
              type="text"
              name="ciudad"
              value={newAddress.ciudad}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Código Postal:</label>
            <input
              type="text"
              name="codigo_postal"
              value={newAddress.codigo_postal}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>País:</label>
            <select value={newAddress.id_pais} onChange={handleCountryChange}>
              <option value="">Seleccionar País</option>
              {countries.map((country) => (
                <option key={country.id} value={country.id}>
                  {country.nombre_pais}
                </option>
              ))}
            </select>
          </div>
          <div>
            <button onClick={handleSaveAddress}>Guardar</button>
            <button onClick={handleCancelAddress}>Cancelar</button>
          </div>
        </div>
      )}
      {/* Mostrar el formulario de método de pago si se activa el estado correspondiente */}
      {showPaymentForm && (
        <div>
          <h3>Datos del Método de Pago</h3>
          <div>
            <label>Número de Tarjeta:</label>
            <input
              type="text"
              name="numero_tarjeta"
              value={newPaymentMethod.numero_tarjeta}
              onChange={handlePaymentInputChange}
            />
          </div>
          <div>
            <label>CVV:</label>
            <input
              type="text"
              name="cvv"
              value={newPaymentMethod.cvv}
              onChange={handlePaymentInputChange}
            />
          </div>
          <div>
            <label>Nombre del Portador:</label>
            <input
              type="text"
              name="nombre_portador"
              value={newPaymentMethod.nombre_portador}
              onChange={handlePaymentInputChange}
            />
          </div>
          <div>
            <label>Fecha de Expiración (MM/YY):</label>
            <input
              type="text"
              name="fecha_expiracion"
              value={newPaymentMethod.fecha_expiracion}
              onChange={handlePaymentInputChange}
            />
          </div>
          <div>
            <button onClick={handlePaymentFormSubmit}>Guardar Método de Pago</button>
            <button onClick={handleCancelPayment}>Cancelar</button>
          </div>
        </div>
      )}
      {/* Elementos adicionales para la dirección y checkout */}
      <div className="additional-elements">
        Dirección:   
        <select>
          {addresses.map((address, index) => (
            <option key={index} value={index}>{`${address.ciudad}, ${address.direccion}`}</option>
          ))}
        </select>
        <button onClick={handleAddNewAddress}>Agregar Nueva Dirección</button>
        <div>
          <label>
            Método de Envío:
            <select value={shippingMethod} onChange={handleShippingMethodChange}>
              {shippingMethods.map((method, index) => (
                <option key={index} value={method.nombre}>{`${method.nombre} - Q.${method.precio}`}</option>
              ))}
            </select>
            {deliveryTime}
          </label>
        </div>
        <div>
          <label>Total de la Compra:</label>
          <input type="text" value={`Q.${calculateTotal()}`} readOnly />
        </div>
        <button onClick={handleCheckout}>Checkout</button>
      </div>
    </div>
  );

};

export default CartItems;
