import React from 'react';
import './Item.css';
import { Link } from 'react-router-dom';

const Item = (props) => {
  return (
    <div className='item'>
      <Link to={`/productsI/${props.id}`} style={{ textDecoration: 'none' }}>
        <img onClick={window.scrollTo(0, 0)} src={props.image1} alt="products" />
      </Link>
      <p>{props.name}</p>
      <p>{props.description}</p>
    </div>
  );
};

export default Item;
