import React, { useState, useEffect } from "react";
import "./CSS/LoginSignup.css";

const LoginSignup = () => {
  const [state, setState] = useState("Login");
  const [loginFormData, setLoginFormData] = useState({
    correo: "",
    contrase単a: ""
  });
  const [signupFormData, setSignupFormData] = useState({
    nombre: "",
    apellido: "",
    correo: "",
    contrase単a: "",
    telefono: "",
    direccion: "",
    estado: "",
    ciudad: "",
    codigo_postal: "",
    id_pais: ""
  });
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    fetch("http://localhost:4000/countries")
      .then((resp) => resp.json())
      .then((data) => {
        setCountries(data);
      })
      .catch((error) => {
        console.error("Error fetching countries:", error);
      });
  }, []);

  const changeHandler = (e, formType) => {
    if (formType === "login") {
      setLoginFormData({ ...loginFormData, [e.target.name]: e.target.value });
    } else {
      setSignupFormData({ ...signupFormData, [e.target.name]: e.target.value });
    }
  };

  const login = async () => {
    const response = await fetch("http://localhost:4000/login", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(loginFormData),
    });
    const data = await response.json();

    if (data.success) {
      localStorage.setItem("auth-token", data.token);
      window.location.replace("/");
    } else {
      alert(data.errors);
    }
  };

  const signup = async () => {
    const response = await fetch("http://localhost:4000/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(signupFormData),
    });
    const data = await response.json();

    if (data.success) {
      localStorage.setItem("auth-token", data.token);
      window.location.replace("/");
    } else {
      alert(data.errors);
    }
  };

  return (
    <div className="loginsignup">
      <div className="loginsignup-container">
        <h1>{state}</h1>
        <div className="loginsignup-fields">
          {state === "Sign Up" && (
            <>
              <input
                type="text"
                placeholder="Nombre"
                name="nombre"
                value={signupFormData.nombre}
                onChange={(e) => changeHandler(e, "signup")}
              />
              <input
                type="text"
                placeholder="Apellido"
                name="apellido"
                value={signupFormData.apellido}
                onChange={(e) => changeHandler(e, "signup")}
              />
              <input
                type="text"
                placeholder="Telefono"
                name="telefono"
                value={signupFormData.telefono}
                onChange={(e) => changeHandler(e, "signup")}
              />
              <input
                type="text"
                placeholder="Direcci坦n"
                name="direccion"
                value={signupFormData.direccion}
                onChange={(e) => changeHandler(e, "signup")}
              />
              <select name="id_pais" value={signupFormData.id_pais} onChange={(e) => changeHandler(e, "signup")}>
                <option value="">Seleccione un pa鱈s</option>
                {countries.map((country) => (
                  <option key={country.id} value={country.id}>
                    {country.nombre_pais}
                  </option>
                ))}
              </select>
              <input
                type="text"
                placeholder="Ciudad"
                name="ciudad"
                value={signupFormData.ciudad}
                onChange={(e) => changeHandler(e, "signup")}
              />
              <input
                type="text"
                placeholder="Estado"
                name="estado"
                value={signupFormData.estado}
                onChange={(e) => changeHandler(e, "signup")}
              />
              <input
                type="text"
                placeholder="C坦digo Postal"
                name="codigo_postal"
                value={signupFormData.codigo_postal}
                onChange={(e) => changeHandler(e, "signup")}
              />
            </>
          )}
          <input
            type="email"
            placeholder="Correo"
            name="correo"
            value={state === "Login" ? loginFormData.correo : signupFormData.correo}
            onChange={(e) => changeHandler(e, state === "Login" ? "login" : "signup")}
          />
          <input
            type="password"
            placeholder="Contrase単a"
            name="contrase単a"
            value={state === "Login" ? loginFormData.contrase単a : signupFormData.contrase単a}
            onChange={(e) => changeHandler(e, state === "Login" ? "login" : "signup")}
          />

<button onClick={() => (signup())}>
          Continue
        </button>
        </div>



        {state === "Login" ? (
          <p className="loginsignup-login">
            Create an account?{" "}
            <span onClick={() => setState("Sign Up")}>Click here</span>
          </p>
        ) : (
          <p className="loginsignup-login">
            Already have an account?{" "}
            <span onClick={() => setState("Login")}>Login here</span>
          </p>
        )}

        <div className="loginsignup-agree">
          <input type="checkbox" name="" id="" />
          <p>
            By continuing, I agree to the terms of use & privacy policy.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginSignup;
