import React, { useContext, useRef, useState, useEffect } from 'react';
import './Navbar.css';
import { Link } from 'react-router-dom';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';
import { ShopContext } from '../../Context/ShopContext';
import nav_dropdown from '../Assets/nav_dropdown.png';

const Navbar = () => {
  const { getTotalCartItems } = useContext(ShopContext);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const menuRef = useRef();

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://localhost:4000/ParentCategoriesNav');
        if (response.ok) {
          const data = await response.json();
          setCategories(data);
        } else {
          throw new Error('Error al obtener las categorías');
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchCategories();
  }, []);

  const handleCategoryClick = async (categoryId, event) => {
    event.preventDefault();
    if (selectedCategory === categoryId) {
      setSelectedCategory(null);
    } else {
      setSelectedCategory(categoryId);
    }
  };

  const renderCategories = (categories) => {
    return categories.map((category) => (
      <li key={category.id}>
        <a href="/" onClick={(e) => handleCategoryClick(category.id, e)}>
          {category.nombre_categoria}
        </a>
        {selectedCategory === category.id && category.hasChildren && (
          <div className='submenu'>
            <ul>
              {renderCategories(category.children)}
            </ul>
          </div>
        )}
      </li>
    ));
  };

  return (
    <div className='nav'>
      <Link to='/' onClick={() => setSelectedCategory(null)} style={{ textDecoration: 'none' }} className='nav-logo'>
        <img src={logo} alt='logo' />
        <p>SHOPPER</p>
      </Link>
      <img onClick={() => setSelectedCategory(null)} className='nav-dropdown' src={nav_dropdown} alt='' />
      <ul ref={menuRef} className='nav-menu'>
        {renderCategories(categories)}
      </ul>
      <div className='nav-login-cart'>
        {localStorage.getItem('auth-token') ? (
          <button onClick={() => {localStorage.removeItem('auth-token'); window.location.replace('/');}}>Logout</button>
        ) : (
          <Link to='/login' style={{ textDecoration: 'none' }}>
            <button>Login</button>
          </Link>
        )}
        <Link to='/cart'>
          <img src={cart_icon} alt='cart' />
        </Link>
        <div className='nav-cart-count'>{getTotalCartItems()}</div>
      </div>
    </div>
  );
};

export default Navbar;
